mapper.select:
	 	select tno, company, ititle, h_goods.gno, goods_name,  to_char(goods_price,'999,999,999,999') goods_price, goods_simage
   		from h_interior, h_goods, h_tag
   		where h_interior.ino = h_goods.ino and h_goods.gno = h_tag.gno and h_tag.pno=#{pno}

	 	select count(*)
   		from h_interior, h_goods, h_tag
   		where h_interior.ino = h_goods.ino and h_goods.gno = h_tag.gno and h_tag.pno=#{pno}
